/* BREADCRUMBS */

-div.breadcrumbs {
+ol.breadcrumbs {
     background: var(--breadcrumbs-bg);
     padding: 10px 40px;
     border: none;
     color: var(--breadcrumbs-fg);
-    text-align: left;
+    margin-top: 0;
+    margin-bottom: 0;
+}
+
+ol.breadcrumbs li {
+    display: inline-block;
+    font-size: 0.875rem;
+    padding: 0;
+    line-height: 0;
+}
+
+ol.breadcrumbs li:not(:last-child)::after {
+    content: ' \203A ' / '';
+}
+
+ol.breadcrumbs li:last-child a {
+    color: var(--breadcrumbs-fg);
+    text-decoration: none !important;
+    cursor: default;
 }

