+  constructor(scene: Scene) {
+    this.scene = scene;
+    this.updateState();
+  }
+
+  select = (connection: ConnectionState | undefined) => {
+    if (connection === this.selection.value) {
+      return;
+    }
+    this.selection.next(connection);
