+    this.state = getConnections(this.scene.byName);
+
+    const s = this.selection.value;
+    if (s) {
+      for (let c of this.state) {
+        if (c.source === s.source && c.index === s.index) {
+          this.selection.next(c);
+          break;
+        }
+      }
+    }

