+impl<T> From<T> for SetOnce<T> {
+    fn from(value: T) -> Self {
+        SetOnce {
+            value_set: AtomicBool::new(true),
+            value: UnsafeCell::new(MaybeUninit::new(value)),
+            notify: Notify::new(),
+        }
+    }
+}
